mapper.select:
select book_id ,stock_num from v_stock



and book_id in(select book_id from t_book where book_Name like concat('%','${bookName}','%')) 




and stock_num between ${min} and ${max}

select
from t_stock
and purchase_item_id in( select purchase_item_id from t_purchase_item where purchase_id in( select purchase_id from t_purchase where purchase_batch like concat('%','${purchaseBatch}','%') ))
and book_id in( select book_id from t_book where book_name like concat('%','${bookName}','%') )
and store_house_id = ${storeHouseId}
AND stock_time >= '${startTime}'
AND stock_time <='${endTime}'
AND stock_num >= ${min}
AND stock_num <= ${max}

select

from v_stock


		AND stock_num >= ${min}


		AND stock_num <= ${max}

select

from t_stock where book_id=${bookId}

		AND stock_num >= ${min}


		AND stock_num <= ${max}
